import React, { useState, useEffect } from 'react';
import queryString from 'query-string';
import Loading from '../../components/Loading/Loading';
import { toast } from 'react-toastify';
import ToastMessage from '../../components/ToastMessage/ToastMessage';
import { Redirect } from 'react-router';
import { connect } from 'react-redux';
import * as actions from '../../redux';
import './Verification.css';

const Verification = (props) => {
  const [isVerified, setIsVerified] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [isRedirect, setIsRedirect] = useState(false);
  const params = queryString.parse(props.location.search);
  const email = params.email;
  const token = params.token;

  useEffect(() => {
    if (email && token && !isLoading) {
      setIsLoading(true);
      props.userVerification({
        email: email,
        token: token
      });
    }

    if (props.success) {
      setIsLoading(false);
      setIsVerified(true);
    }
  }, [props, email, token, isLoading]);

  const closeToast = () => {
    setIsRedirect(true);
  }

  if (isVerified && !isRedirect) {
    return (
      toast("The close button change when Chuck Norris display a toast"), {
        onClose: closeToast()
      }
    );
  }

  if (isRedirect) {
    return <Redirect push to="/Login" />;
  }

  if (isLoading) {
    return (
      <Loading />
    );
  } else if (!isLoading && !isVerified) {
    return (
      <div className="verification">
        <div>You must send another email to verify your account.</div>
        <button>Send email</button>
      </div>
    );
  }
};

function mapStateToProps({ usersReducer }) {
  return {
    error: usersReducer.error,
    isLoading: usersReducer.isLoading,
    success: usersReducer.success
  };
}

export default connect(mapStateToProps, actions)(Verification);
