import React, { Component } from 'react';
import { connect } from 'react-redux';
import * as actions from '../../redux';
// import { ROOT_URL } from '../../config/networkSettings';
// import io from "socket.io-client";
import './Chatroom.css';
// import person from '../../assets/images/person-icon.png';
// import general from '../../assets/images/general.png';
// import AuthNavigation from '../../components/AuthNavigation/AuthNavigation';


// import { Link } from 'react-router-dom';
import chatot from '../../assets/images/chatot.png';
import friends from '../../assets/images/friends.png';

class Chatroom extends Component {
  constructor(props) {
    super(props);

    this.state = {
      id: "",
      username: "",
      message: "",
      type: "",
      messages: [],
      users: [],
      clickedUsername: "",
      clickedType: "",
      privateMessages: [],
      personalMessages: [],
      userDetails: false,
      socketId: "",
      currentSocket: ""
    }
  }

  async componentDidMount() {
    this.props.currentUser();
  }

  async componentWillReceiveProps(nextProps) {
    if (nextProps.user) {
      const { id, username, type } = nextProps.user;
      this.setState({
        id: id,
        username: username,
        type: type
      });
    }
  }

  render() {
    return (
      <div className="chatroom">
        <div className="sidebar">
          <div className="sidebar-container">
            <span className="sidebar-select"></span>
            <img className="sidebar-logo" src={chatot} alt="chatter-icon-logo" />
          </div>
          <div className="sidebar-border" />
          <div className="sidebar-container">
            <img className="sidebar-logo" src={chatot} alt="chatter-icon" />
          </div>
        </div>
        <div className="sidebarleft">
          <div className="sidebarleft-container">
            <input placeholder="Find or start a conversation"></input>
          </div>
          <div className="sidebarleft-border" />
          <div className="sidebarleft-friendscontainer">
            <img className="sidebarleft-logo" src={friends} alt="friends-icon" />
            <span className="sidebarleft-friends">Friends</span>
          </div>
          <div className="sidebarleft-border" />
          <div className="sidebarleft-directmessages">
            <span>Private Messages</span>
          </div>
          <div className="sidebarleft-bordertwo" />
          <span style={{ color: 'white' }}>{this.state.username}</span>
        </div>
        <div className="chatarea">
          <div className="chatarea-container">
            <input placeholder="Send a message to people in the chatroom"></input>
          </div>
        </div>
        <div className="sidebarright">
          <div className="sidebarright-container">
            <input placeholder="Filter users in server"></input>
          </div>
          <div className="sidebarright-border" />
          <div className="sidebarright-authority">
            <span>Room Owners</span>
          </div>
          <div className="sidebarright-bordertwo" />
          <div className="sidebarright-authority">
            <span>Administrators</span>
          </div>
          <div className="sidebarright-bordertwo" />
          <div className="sidebarright-authority">
            <span>Moderators</span>
          </div>
          <div className="sidebarright-bordertwo" />
          <div className="sidebarright-authority">
            <span>Voice</span>
          </div>
          <div className="sidebarright-bordertwo" />
          <div className="sidebarright-authority">
            <span>Users</span>
          </div>
          <div className="sidebarright-bordertwo" />
        </div>
      </div>
    );
  }
}

function mapStateToProps({ usersReducer }) {
  return {
    error: usersReducer.error,
    isLoading: usersReducer.isLoading,
    success: usersReducer.success,
    user: usersReducer.user
  };
}

export default connect(mapStateToProps, actions)(Chatroom);
