import React, { forwardRef } from "react";
import "./DropItem.css";

export default forwardRef(({ children, heading }, ref) => {
  console.log(children, ref);
  if (children.current)
  return (
    <div className="hovercontainer" ref={ref}>
      <div className="children-drop">{children}</div>
    </div>
  );
});
