import secret from '../config/secret';
import jwt from 'jsonwebtoken';
export const ROOT_URL = 'http://localhost:5000';
// export const ROOT_URL = 'https://chattersanctum.com';
export const HASH = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJzdWIiOiIxMjM0NTY3ODkwIiwibmFtZSI6IkpvaG4gRG9lIiwiaWF0IjoxNTE2MjM5MDIyfQ.vfQWFfZPQNJ-gz3qYqHtb3qfuMJsQV02RPckLoQS3So';
export const JWT_TOKEN = 'JWT eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJzdWIiOiIxMjM0NTY3ODkwIiwibmFtZSI6IkpvaG4gRG9lIiwiaWF0IjoxNTE2MjM5MDIyfQ.6p1XVDcBXV904-cP0Dyd6GXsDJ0PMjfnDL1e2mO3N3E';
var token = jwt.sign({
  username: HASH
}, secret);
export const config = {
  headers: { 'Authorization': 'bearer ' + token }
}
