import React, { Component } from 'react';
import { connect } from 'react-redux';
import * as actions from '../../redux';
// import { ROOT_URL } from '../../config/networkSettings';
// import io from "socket.io-client";
import './Dashboard.css';
// import person from '../../assets/images/person-icon.png';
// import general from '../../assets/images/general.png';
// import AuthNavigation from '../../components/AuthNavigation/AuthNavigation';


// import { Link } from 'react-router-dom';
import chatot from '../../assets/images/chatot.png';
import friends from '../../assets/images/friends.png';
import settings from '../../assets/images/settings.png';

class Dashboard extends Component {
  constructor(props) {
    super(props);

    this.state = {
      id: "",
      username: "",
      active: false,
      type: "",
      imageUrl: "",
      message: "",
      messages: [],
      users: [],
      chatroom: "",
      clickedUsername: "",
      clickedType: "",
      privateMessages: [],
      personalMessages: [],
      userDetails: false,
      socketId: "",
      currentSocket: ""
    }
  }

  async componentDidMount() {
    this.props.currentUser();
    this.props.getUsers();
  }

  async componentWillReceiveProps(nextProps) {
    if (nextProps.user) {
      const { id, username, active/*, type*/ } = nextProps.user;
      this.setState({
        id: id,
        username: username,
        active: active,
        type: 'admin'
      });
    }

    if (nextProps.users && nextProps.users.length) {
      const userList = [];
      for(let i = 0; i < nextProps.users.length; i++) {
        nextProps.users[i].type = 'admin';
        userList.push(nextProps.users[i]);
      }
      this.setState({ users: userList });
    }
  }

  setChatroom(chatroom) {
    this.setState({ chatroom: chatroom });
  }

  render() {
    return (
      <div className="chatroom">
        <div className="sidebar">
          <div className="sidebar-container">
            <span className="sidebar-select"></span>
            <img className="sidebar-logo" src={chatot} alt="chatter-icon-logo" onClick={() => { this.setChatroom("") }} />
          </div>
          <div className="sidebar-border" />
          <div className="sidebar-container">
            <img className="sidebar-logo" src={chatot} alt="chatter-icon" onClick={() => { this.setChatroom(this.state.username) }} />
          </div>
        </div>
        <div className="sidebarleft">
          <div className="sidebarleft-container">
            <input placeholder="Find or start a conversation"></input>
          </div>
          <div className="sidebarleft-border" />
          <div className="sidebarleft-friendscontainer">
            <img className="sidebarleft-logo" src={friends} alt="friends-icon" />
            <span className="sidebarleft-friends">Friends</span>
          </div>
          <div className="sidebarleft-border" />
          <div className="sidebarleft-directmessages">
            <span>Private Messages</span>
          </div>
          <div className="sidebarleft-bordertwo" />
          <div className="userinfo">
            <div className="username">
              <img className="username-image" src={this.state.imageUrl ? this.state.imageUrl : chatot} alt="username-icon" />
            </div>
            {this.state.active ? <div className="userinfo-online"></div> : null}
            <span style={{ color: 'white' }}>{this.state.username}</span>
            <img className="settings-image" src={settings} alt="settings-icon" />
          </div>
        </div>
        {this.state.chatroom === this.state.username ?
          <div>
            <div className="chatarea">
              <div className="chatarea-container">
                <input placeholder="Send a message!"></input>
              </div>
            </div>
            <div className="sidebarright">
              <div className="sidebarright-container">
                <input placeholder="Filter users in server"></input>
              </div>
              <div className="sidebarright-border" />
              <div className="sidebarright-authority">
                <span>Room Owners</span>
              </div>
              <div className="sidebarright-bordertwo" />
              {this.state.users.length > 0 ? this.state.users.map((user, index)  => {
                return (
                  <div key={index} className={user.type === 'owner' ? "sidebarright-usercontainer" : ""}>
                    {user.type === 'owner' ? <div className="username">
                      <img className="username-image" src={user.imageUrl ? user.imageUrl : chatot} alt="username-icon" />
                    </div> : null}
                    {user.type === 'owner' && user.active ? <div className="userinfo-online"></div> : null}
                    {user.type === 'owner' && !user.active ? <div className="userinfo-offline"></div> : null}
