                  <div key={index} className={user.type === 'admin' ? "sidebarright-usercontainer" : ""}>
                    {user.type === 'admin' ? <div className="username">
                      <img className="username-image" src={user.imageUrl ? user.imageUrl : chatot} alt="username-icon" />
