import React, { useEffect, useRef } from 'react';
import './InviteModal.css';

const InviteModal = (props) => {
  const ref = useRef();
  useOnClickOutside(ref, () => props.setInviteModal(false));

  return (
    <div ref={ref} className="invitemodal-container">
      <p className="invitemodal-container-title">Create Invite</p>
      <button className="invitemodal-container-cancel" onClick={() => { props.setInviteModal(false); }}>Cancel</button>
      <button className="invitemodal-container-create" onClick={() => { props.createNewInvite(); }}>Create</button>
    </div>
  );
}

const useOnClickOutside = (ref, handler) => {
  useEffect(() => {
    const listener = event => {
      // Do nothing if clicking ref's element or descendent elements
      if (!ref.current || ref.current.contains(event.target)) {
        return;
      }

      handler(event);
    };

    document.addEventListener('mousedown', listener);
    document.addEventListener('touchstart', listener);

    return () => {
      document.removeEventListener('mousedown', listener);
      document.removeEventListener('touchstart', listener);
    };
  }, [ref, handler]);
}

export default InviteModal;
