import React, { useState, useEffect } from 'react';
import queryString from 'query-string';
import Loading from '../../components/Loading/Loading';
// import Navigation from '../../components/Navigation/Navigation';
import { Redirect } from 'react-router';
import { connect } from 'react-redux';
import * as actions from '../../redux';
import './Verification.css';

const Verification = (props) => {
  const [isLoading, setIsLoading] = useState(false);
  const params = queryString.parse(props.location.search);
  const email = params.email;
  const token = params.token;

  if (email && token) {
    setIsLoading(true);
    props.userVerification({
      email: email,
      token: token
    });
  }

  if (props.success) {
    return <Redirect push to="/Login" />;
  }

  if (isLoading) {
    return (
      <Loading />
    );
  }

  if (!isLoading && !verified) {
    return (
      <div className="verification">
        <span>You must send another email to verify your account.</span>
        <button>Send email</button>
      </div>
    );
  }
};

function mapStateToProps({ usersReducer }) {
  return {
    error: usersReducer.error,
    isLoading: usersReducer.isLoading,
    success: usersReducer.success
  };
}

export default connect(mapStateToProps, actions)(Verification);
