import axios from 'axios';
import { ROOT_URL } from '../../../config/networkSettings';

import {
  CREATING_SERVER,
  CREATE_SERVER_FAIL,
  CREATE_SERVER_SUCCESS,
  DELETING_SERVER,
  DELETE_SERVER_FAIL,
  DELETE_SERVER_SUCCESS
} from '../../types';

// Initial States
export const initialState = {
  isLoading: false,
  error: false,
  success: false,
  user: null
};

// Reducers
export default (state = initialState, action) => {
  switch (action.type) {
    case CREATING_SERVER:
      return {
        ...state, isLoading: true, error: false, success: false
      };
    case CREATE_SERVER_SUCCESS:
      return {
        ...state, isLoading: false, error: false, success: true
      };
    case CREATE_SERVER_FAIL:
      return {
        ...state, isLoading: false, error: true, success: false
      };
    case DELETING_SERVER:
      return {
        ...state, isLoading: true, error: false, success: false
      };
    case DELETE_SERVER_SUCCESS:
      return {
        ...state, isLoading: false, error: false, success: true, user: action.payload
      };
    case DELETE_SERVER_FAIL:
      return {
        ...state, isLoading: false, error: true, logout: false
      };
    default:
      return state;
  }
};

// Actions
export const createServer = params => async dispatch => {
  dispatch({ type: CREATING_SERVER });
  try {
    const response = await axios.post(`${ROOT_URL}/api/v1/createServer`, params);
    if(response.data) {
      dispatch({ type: CREATE_SERVER_SUCCESS });
    } else {
      dispatch({ type: CREATE_SERVER_FAIL });
    }
  } catch(err) {
    dispatch({ type: CREATE_SERVER_FAIL });
  }
};

export const deleteServer = params => async dispatch => {
  dispatch({ type: DELETING_SERVER });
  try {
    const response = await axios.post(`${ROOT_URL}/api/v1/deleteServer`, params);
    if (response.data) {
      dispatch({ type: DELETE_SERVER_SUCCESS });
    } else {
      dispatch({ type: DELETE_SERVER_FAIL });
    }
  } catch (err) {
    dispatch({ type: DELETE_SERVER_FAIL });
  }
};
