            {isAdmin ? <div className="sidebarleftchat-container-dropdown" onClick={() => { showServerSettings(!serverSettings); }}>{!serverSettings ? <i className="channelarrow down"></i> : <span className="cancel">&#10005;</span>}</div> : null}
            {serverSettings ?
