import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as actions from '../../redux';
import { Redirect } from 'react-router';
import { toast } from 'react-toastify';
import ToastMessage from '../../components/ToastMessage/ToastMessage';
import './Login.css';

const Login = (props) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [notVerified, setNotVerified] = useState(false);
  const [isRedirect, setIsRedirect] = useState(false);
  const [resetModel, setResetModal] = useState(false);
  const [resetEmail, setResetEmail] = useState('');

  useEffect(() => {
    if (props.error) {
      toast.dismiss();
      toast.error('Please check your username and password, and try again!', { position: 'bottom-center' });
    }

    if (props.forgotPassError) {
      toast.dismiss();
      toast.error('Error Requesting Password!', { position: toast.POSITION.TOP_CENTER });
    }

    if (props.forgotPassSuccess) {
      setResetModal(false);
      setResetEmail('');
      toast.dismiss();
      toast.success('Success, please check your email for instructions!', { position: toast.POSITION.TOP_CENTER });
    }

    if (props.notVerified && !notVerified) {
      setNotVerified(true);
    }

    if (props.error) {
      toast.dismiss();
      toast.error('Please check your email and password, and try again!', { position: 'bottom-center' });
    }
    props.resetUserValues();
  }, [props, notVerified]);

  const closeToast = () => {
    setIsRedirect(true);
  }

  if (notVerified && !isRedirect) {
    toast.error('Your account has not been verified!', {
      position: 'bottom-center'
    });

    setTimeout(() => {
      closeToast();
    }, 3000);
  }

  if (isRedirect) {
    return <Redirect push to="/Verification" />;
  }

  if (props.success) {
    return <Redirect push to="/Dashboard" />;
  }

  const userLogin = async() => {
    const params = {
      email: email,
      password: password
    }
    if(!email && !password) {
      toast.error('Email and password are required.', { position: 'bottom-center' });
    } else {
      props.userLogin(params);
    }
  }

  const forgotPassword = () => {
    setResetModal(true);
  }

  const resetModalDefault = () => {
    setResetModal(false);
    setResetEmail('');
  }

  return (
    <div className="authcontainerlogin">
      <ToastMessage />
      <span>Login to chatter!</span>
      <div className="authcontainerlogin__section">
        <span>Email</span>
        <input onChange={event => setEmail(event.target.value)} value={email} />
        <span>Password</span>
        <input onChange={event => setPassword(event.target.value)} value={password} type="password" />
        <p onClick={forgotPassword}>Forgot your password?</p>
        <button onClick={userLogin}>Login</button>
        <Link to="/Signup"><p className="register">Need an account? Register.</p></Link>
      </div>

      <div className="resetModel" style={{ display: (resetModel === false ? 'none' : null) }}>
        <span className="resetModel__close" onClick={resetModalDefault}><FaClose /></span>
        <span className="resetModel__heading">Reset Password</span>
        <input className="resetModel__password" placeholder="Email" onChange={event => setResetEmail(event.target.value)} value={resetEmail} />
        <button className="resetModel__submit" onClick={resetSubmit}>Submit</button>
      </div>
    </div>
  );
};

function mapStateToProps({ usersReducer }) {
  return {
    error: usersReducer.error,
    isLoading: usersReducer.isLoading,
    success: usersReducer.success,
    notVerified: usersReducer.notVerified,
    forgotPassError: usersReducer.forgotPassError,
    forgotPassSuccess: usersReducer.forgotPassSuccess,
  }
}

export default connect(mapStateToProps, actions)(Login);
