import React, { useState, useRef } from "react";
import useDrop from "../../hooks/useDrop";
import "./DropItem.css";
import View from "./View";
import HoverView from './HoverView'

export default ({ children, heading, onDrop }) => {
  const [showHover, setShowHover] = useState(false);
  const dropRef = useRef();
  const { dropState, droppedItem } = useDrop({
    ref: dropRef,
    onDrop
  });
  // console.log(children);
  // console.log(children[0]);
  // console.log(children[0].props.data.text);
  // console.log(dropRef);
  // console.log('dropstate: ', dropState);
  if (dropState === 'dragging over' && dropRef && showHover !== true && children && children.length) {
    let count = 0;
    for (let i = 0; i < children.length; i++) {
      if (children[i].props && children[i].props.data.text === dropRef.current.innerText) {
        count++;
      }
    }
    if (count > 0 && showHover === true) {
      setShowHover(false);
    } else if (count === 0 && showHover === false) {
      setShowHover(true);
    }
  }
  if (showHover === true) {
    return (
      <HoverView ref={dropRef} dropState={dropState} heading={heading} droppedItem={droppedItem}>
        {children}
      </HoverView>
    );
  } else {
    return (
      <View ref={dropRef} dropState={dropState} heading={heading} droppedItem={droppedItem}>
        {children}
      </View>
    );
  }
};
