import React, { useRef } from 'react';
import useOnClickOutside from '../../utils/useOnClickOutside';
import './EditAccount.css';

const EditAccount = (props) => {
  const ref = useRef();
  useOnClickOutside(ref, () => props.setAccountModalOpen(false));

  return (
    <div ref={ref} className="editaccount">
      <h1 className="editaccount-title">My Account</h1>
      <div>
        <span className="editaccount-info">Email</span>
        <input onChange={(event) => { props.setEditEmail(event.target.value); }} value={props.editEmail} />
      </div>
      <div>
        <span className="editaccount-info">Username</span>
        <input onChange={(event) => { props.setEditUsername(event.target.value); }} value={props.editUsername} />
      </div>
      <div className="editaccount-actions">
        <p onClick={props.setAccountModalOpen}>&larr; Back</p>
        <button onClick={() => { props.saveAccountInfo()}}>Save</button>
      </div>
    </div>
  );
}

export default EditAccount;
